import { Button } from './ui/button.tsx';
import { Home, Search, PlusCircle, MessageSquare, User } from 'lucide-react';

type Tab = 'feed' | 'discover' | 'create' | 'messages' | 'profile';

interface BottomNavigationProps {
  currentTab: Tab;
  onTabChange: (tab: Tab) => void;
}

export function BottomNavigation({ currentTab, onTabChange }: BottomNavigationProps) {
  return (
    <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 py-2 px-4">
      <div className="flex justify-between items-center max-w-2xl mx-auto">
        <button 
          onClick={() => onTabChange('feed')} 
          className={`flex flex-col items-center px-3 py-1 ${currentTab === 'feed' ? 'text-blue-500' : 'text-gray-500'}`}
        >
          <Home className="h-6 w-6" />
          <span className="text-xs mt-1">Feed</span>
        </button>
        
        <button 
          onClick={() => onTabChange('discover')} 
          className={`flex flex-col items-center px-3 py-1 ${currentTab === 'discover' ? 'text-blue-500' : 'text-gray-500'}`}
        >
          <Search className="h-6 w-6" />
          <span className="text-xs mt-1">Discover</span>
        </button>
        
        <button 
          onClick={() => onTabChange('create')} 
          className={`flex flex-col items-center px-3 py-1 ${currentTab === 'create' ? 'text-blue-500' : 'text-gray-500'}`}
        >
          <div className="relative">
            <div className={`absolute inset-0 ${currentTab === 'create' ? 'bg-blue-500' : 'bg-gray-200'} rounded-full -m-1`}></div>
            <PlusCircle className={`h-7 w-7 relative z-10 ${currentTab === 'create' ? 'text-white' : 'text-gray-600'}`} />
          </div>
          <span className="text-xs mt-1">Create</span>
        </button>
        
        <button 
          onClick={() => onTabChange('messages')} 
          className={`flex flex-col items-center px-3 py-1 ${currentTab === 'messages' ? 'text-blue-500' : 'text-gray-500'}`}
        >
          <MessageSquare className="h-6 w-6" />
          <span className="text-xs mt-1">Messages</span>
        </button>
        
        <button 
          onClick={() => onTabChange('profile')} 
          className={`flex flex-col items-center px-3 py-1 ${currentTab === 'profile' ? 'text-blue-500' : 'text-gray-500'}`}
        >
          <User className="h-6 w-6" />
          <span className="text-xs mt-1">Profile</span>
        </button>
      </div>
    </div>
  );
}
