import { useState, useEffect } from 'react'
import { supabase } from './utils/supabase.ts'

function Page() {
  const [connectionStatus, setConnectionStatus] = useState('Testing...')

  useEffect(() => {
    async function testConnection() {
      try {
        // Simple query just to test connection
        const { data, error } = await supabase.from('todos').select('count')

        if (error) throw error
        setConnectionStatus('Connected to Supabase successfully!')
        console.log('Connection test succeeded:', data)
      } catch (error) {
        console.error('Connection error:', error)
        setConnectionStatus(`Error: ${error.message || 'Unknown error'}`)
      }
    }

    testConnection()
  }, [])

  return (
    <div style={{ padding: '20px', maxWidth: '500px', margin: '0 auto' }}>
      <h1>Supabase Connection Test</h1>
      <div style={{ 
        padding: '15px',
        borderRadius: '5px',
        background: connectionStatus.includes('Error') ? '#ffebee' : '#e8f5e9'
      }}>
        <p>{connectionStatus}</p>
      </div>
    </div>
  )
}

export default Page
