import { useState, useEffect } from 'react';
import { LogOut, Search, Users, Video, Search as SearchIcon, Calendar, Clock, MapPin } from 'lucide-react';
import { supabase } from '../utils/supabase';
import { Button } from './ui/button';
import { Badge } from './ui/badge';
import { Skeleton } from './ui/skeleton';
import { Avatar } from './ui/avatar';
import { EmptyState } from './EmptyState';

interface Musician {
  id: string;
  name: string;
  username: string;
  bio: string;
  avatar: string;
  instruments: string[];
  genres: string[];
  experience: 'Beginner' | 'Intermediate' | 'Advanced' | 'Professional';
  location: string;
  isAvailable: boolean;
  followers: number;
  following: number;
  projects: number;
}

interface Post {
  id: string;
  musician: Musician;
  content: string;
  type: 'collaboration' | 'showcase' | 'looking-for' | 'jam-session';
  media?: {
    type: 'audio' | 'video' | 'image';
    url: string;
    duration?: string;
  };
  tags: string[];
  likes: number;
  comments: number;
  timestamp: string;
  isLiked: boolean;
}

interface FeedProps {
  currentUser: Musician;
  onSignOut?: () => void;
}

export function Feed({ currentUser, onSignOut }: FeedProps) {
  const [posts, setPosts] = useState<Post[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedFilter, setSelectedFilter] = useState('all');
  
  // Fetch posts from Supabase when component mounts
  useEffect(() => {
    fetchPosts();
    
    // Set up real-time listener for new posts
    const postsSubscription = supabase
      .channel('public:posts')
      .on('postgres_changes', 
        { event: '*', schema: 'public', table: 'posts' }, 
        payload => {
          if (payload.eventType === 'INSERT') {
            fetchPostDetails(payload.new.id).then(post => {
              if (post) setPosts(current => [post, ...current]);
            });
          } else if (payload.eventType === 'UPDATE') {
            fetchPostDetails(payload.new.id).then(updatedPost => {
              if (updatedPost) {
                setPosts(current => current.map(post => 
                  post.id === updatedPost.id ? updatedPost : post
                ));
              }
            });
          } else if (payload.eventType === 'DELETE') {
            setPosts(current => current.filter(post => post.id !== payload.old.id));
          }
        }
      )
      .subscribe();
      
    // Clean up subscription on unmount
    return () => {
      supabase.removeChannel(postsSubscription);
    };
  }, []);
  
  // Function to fetch posts from Supabase
  const fetchPosts = async () => {
    setIsLoading(true);
    try {
      // Fetch posts with musician data
      const { data, error } = await supabase
        .from('posts')
        .select(`
          *,
          musician:user_id (
            id, name, username, avatar, location, experience, instruments
          ),
          likes:post_likes (count),
          comments:post_comments (count)
        `)
        .order('created_at', { ascending: false });
        
      if (error) throw error;
      
      // Format the data to match our Post interface
      const formattedPosts: Post[] = data.map(post => ({
        id: post.id,
        musician: {
          id: post.musician.id,
          name: post.musician.name,
          username: post.musician.username,
          avatar: post.musician.avatar,
          instruments: post.musician.instruments || [],
          experience: post.musician.experience,
          location: post.musician.location,
          isAvailable: true, // Default
          bio: '', // Not needed for feed
          genres: [], // Not needed for feed
          followers: 0, // Not needed for feed
          following: 0, // Not needed for feed
          projects: 0 // Not needed for feed
        },
        content: post.content,
        type: post.type,
        media: post.media,
        tags: post.tags || [],
        likes: post.likes[0]?.count || 0,
        comments: post.comments[0]?.count || 0,
        timestamp: new Date(post.created_at).toLocaleDateString(),
        isLiked: false // Will be updated separately
      }));
      
      // Check which posts the current user has liked
      await checkUserLikes(formattedPosts);
      
      setPosts(formattedPosts);
    } catch (error) {
      console.error('Error fetching posts:', error);
    } finally {
      setIsLoading(false);
    }
  };
  
  // Function to fetch a single post after real-time update
  const fetchPostDetails = async (postId: string): Promise<Post | null> => {
    try {
      const { data, error } = await supabase
        .from('posts')
        .select(`
          *,
          musician:user_id (
            id, name, username, avatar, location, experience, instruments
          ),
          likes:post_likes (count),
          comments:post_comments (count)
        `)
        .eq('id', postId)
        .single();
        
      if (error) throw error;
      
      // Format the post
      const formattedPost: Post = {
        id: data.id,
        musician: {
          id: data.musician.id,
          name: data.musician.name,
          username: data.musician.username,
          avatar: data.musician.avatar,
          instruments: data.musician.instruments || [],
          experience: data.musician.experience,
          location: data.musician.location,
          isAvailable: true,
          bio: '',
          genres: [],
          followers: 0,
          following: 0,
          projects: 0
        },
        content: data.content,
        type: data.type,
        media: data.media,
        tags: data.tags || [],
        likes: data.likes[0]?.count || 0,
        comments: data.comments[0]?.count || 0,
        timestamp: new Date(data.created_at).toLocaleDateString(),
        isLiked: false
      };
      
      // Check if user has liked this post
      const { data: likeData } = await supabase
        .from('post_likes')
        .select()
        .eq('post_id', postId)
        .eq('user_id', currentUser.id);
        
      formattedPost.isLiked = !!likeData?.length;
      
      return formattedPost;
    } catch (error) {
      console.error('Error fetching post details:', error);
      return null;
    }
  };
  
  // Check which posts the current user has liked
  const checkUserLikes = async (postsToCheck: Post[]) => {
    try {
      const { data } = await supabase
        .from('post_likes')
        .select('post_id')
        .eq('user_id', currentUser.id);
        
      if (data) {
        const likedPostIds = new Set(data.map(like => like.post_id));
        postsToCheck.forEach(post => {
          post.isLiked = likedPostIds.has(post.id);
        });
      }
    } catch (error) {
      console.error('Error checking user likes:', error);
    }
  };
  
  // Toggle like on a post
  const handleLike = async (postId: string) => {
    const post = posts.find(p => p.id === postId);
    if (!post) return;
    
    const newIsLiked = !post.isLiked;
    
    // Optimistic update
    setPosts(current => 
      current.map(p => p.id === postId ? 
        { ...p, isLiked: newIsLiked, likes: p.likes + (newIsLiked ? 1 : -1) } : 
        p
      )
    );
    
    try {
      if (newIsLiked) {
        // Add like
        await supabase
          .from('post_likes')
          .insert({ post_id: postId, user_id: currentUser.id });
      } else {
        // Remove like
        await supabase
          .from('post_likes')
          .delete()
          .eq('post_id', postId)
          .eq('user_id', currentUser.id);
      }
    } catch (error) {
      console.error('Error toggling like:', error);
      
      // Revert optimistic update on error
      setPosts(current => 
        current.map(p => p.id === postId ? 
          { ...p, isLiked: !newIsLiked, likes: p.likes + (newIsLiked ? -1 : 1) } : 
          p
        )
      );
    }
  };

  // Filter posts by type
  const filteredPosts = posts.filter(post => {
    return selectedFilter === 'all' || post.type === selectedFilter;
  });
  
  return (
    <div className="max-w-2xl mx-auto px-4 pb-16">
      {/* Header */}
      <header className="py-4 flex justify-between items-center border-b border-gray-200">
        <h1 className="text-2xl font-bold text-gray-800">MusicConnect</h1>
        <button 
          onClick={onSignOut}
          className="flex items-center gap-2 py-2 px-4 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
        >
          <LogOut size={16} /> Sign Out
        </button>
      </header>
      
      {/* Feed Title */}
      <div className="mt-8">
        <h2 className="text-3xl font-bold text-gray-900">Feed</h2>
        <p className="text-gray-600">Discover what's happening in the music community</p>
      </div>
      
      {/* User Avatar */}
      <div className="mt-4">
        <Avatar className="h-16 w-16 border-2 border-white shadow-md">
          <img 
            src={currentUser.avatar} 
            alt={currentUser.name} 
            className="object-cover"
          />
        </Avatar>
      </div>
      
      {/* Search */}
      <div className="relative mt-6">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <Search className="h-5 w-5 text-gray-400" />
        </div>
        <input
          type="text"
          placeholder="Search posts, musicians, or tags..."
          className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md bg-white shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
        />
      </div>
      
      {/* Filter Tabs */}
      <div className="flex border-b border-gray-200 mt-6 overflow-x-auto">
        <button
          onClick={() => setSelectedFilter('all')}
          className={`px-4 py-2 font-medium text-sm ${
            selectedFilter === 'all' 
              ? 'text-blue-600 border-b-2 border-blue-600' 
              : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          All Posts
        </button>
        
        <button
          onClick={() => setSelectedFilter('collaboration')}
          className={`px-4 py-2 font-medium text-sm flex items-center ${
            selectedFilter === 'collaboration' 
              ? 'text-blue-600 border-b-2 border-blue-600' 
              : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          <Users className="w-4 h-4 mr-1" /> Collaborations
        </button>
        
        <button
          onClick={() => setSelectedFilter('showcase')}
          className={`px-4 py-2 font-medium text-sm flex items-center ${
            selectedFilter === 'showcase' 
              ? 'text-blue-600 border-b-2 border-blue-600' 
              : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          <Video className="w-4 h-4 mr-1" /> Showcases
        </button>
        
        <button
          onClick={() => setSelectedFilter('looking-for')}
          className={`px-4 py-2 font-medium text-sm flex items-center ${
            selectedFilter === 'looking-for' 
              ? 'text-blue-600 border-b-2 border-blue-600' 
              : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          <SearchIcon className="w-4 h-4 mr-1" /> Looking For
        </button>
        
        <button
          onClick={() => setSelectedFilter('jam-session')}
          className={`px-4 py-2 font-medium text-sm flex items-center ${
            selectedFilter === 'jam-session' 
              ? 'text-blue-600 border-b-2 border-blue-600' 
              : 'text-gray-500 hover:text-gray-700'
          }`}
        >
          <Calendar className="w-4 h-4 mr-1" /> Jam Sessions
        </button>
      </div>
      
      {/* Empty state or loading state */}
      {isLoading ? (
        <div className="space-y-4 mt-6">
          {[1, 2, 3].map(i => (
            <div key={i} className="border border-gray-200 rounded-md p-4">
              <div className="flex items-start gap-3">
                <Skeleton className="h-12 w-12 rounded-full" />
                <div className="space-y-2 flex-1">
                  <Skeleton className="h-4 w-1/4" />
                  <Skeleton className="h-3 w-1/3" />
                  <Skeleton className="h-24 w-full" />
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : filteredPosts.length === 0 ? (
        <div className="mt-16 text-center">
          <div className="inline-flex h-12 w-12 items-center justify-center rounded-full bg-blue-100 text-blue-600 mb-4">
            <Users className="h-6 w-6" />
          </div>
          <h3 className="text-lg font-medium text-gray-900">No posts yet</h3>
          <p className="mt-1 text-gray-500">
            {selectedFilter === 'all' 
              ? 'Be the first to post and connect with other musicians!' 
              : `No ${selectedFilter} posts available. Create one to get started!`}
          </p>
          <div className="mt-6">
            <button 
              onClick={() => {/* Navigate to create post */}}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700"
            >
              Create a Post
            </button>
          </div>
        </div>
      ) : (
        <div className="mt-6 space-y-6">
          {filteredPosts.map(post => (
            <article key={post.id} className="border border-gray-200 rounded-lg overflow-hidden bg-white">
              {/* Post header */}
              <div className="p-4 border-b border-gray-100">
                <div className="flex items-start">
                  <img 
                    src={post.musician.avatar} 
                    alt=""
                    className="w-12 h-12 rounded-full object-cover mr-3" 
                  />
                  <div className="flex-1">
                    <div className="flex items-center justify-between">
                      <div>
                        <h3 className="font-semibold text-gray-900">{post.musician.name}</h3>
                        <p className="text-sm text-gray-500">@{post.musician.username}</p>
                      </div>
                      <Badge variant="secondary" className="bg-blue-100 text-blue-800 font-normal">
                        {post.musician.experience}
                      </Badge>
                    </div>
                    <div className="flex items-center gap-1 mt-1 text-xs text-gray-500">
                      <MapPin size={12} />
                      <span>{post.musician.location}</span>
                      <span className="mx-1">â€¢</span>
                      <Clock size={12} />
                      <span>{post.timestamp}</span>
                    </div>
                  </div>
                </div>
              </div>
              
              {/* Post content */}
              <div className="p-4">
                <p className="text-gray-800 whitespace-pre-line">{post.content}</p>
                
                {/* Post media */}
                {post.media && (
                  <div className="mt-3 rounded-md overflow-hidden">
                    {post.media.type === 'image' ? (
                      <img 
                        src={post.media.url} 
                        alt=""
                        className="w-full h-auto object-cover" 
                      />
                    ) : post.media.type === 'video' ? (
                      <video 
                        src={post.media.url} 
                        controls
                        className="w-full"
                      />
                    ) : post.media.type === 'audio' ? (
                      <audio 
                        src={post.media.url} 
                        controls
                        className="w-full"
                      />
                    ) : null}
                  </div>
                )}
                
                {/* Tags */}
                <div className="flex flex-wrap gap-1 mt-3">
                  {post.musician.instruments.map(instrument => (
                    <Badge key={instrument} variant="outline" className="bg-gray-50">
                      {instrument}
                    </Badge>
                  ))}
                  {post.tags.map(tag => (
                    <Badge key={tag} variant="outline" className="text-blue-600 bg-blue-50 border-blue-200">
                      #{tag}
                    </Badge>
                  ))}
                </div>
                
                <div className="mt-4 pt-3 border-t border-gray-100 flex justify-between">
                  <div className="flex items-center gap-4">
                    <button 
                      onClick={() => handleLike(post.id)}
                      className="flex items-center gap-1 text-sm"
                    >
                      <svg 
                        xmlns="http://www.w3.org/2000/svg" 
                        width="16" 
                        height="16" 
                        viewBox="0 0 24 24" 
                        fill={post.isLiked ? "currentColor" : "none"} 
                        stroke="currentColor" 
                        strokeWidth="2" 
                        className={post.isLiked ? "text-red-500" : "text-gray-600"}
                      >
                        <path d="M20.84 4.61a5.5 5.5 0 0 0-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 0 0-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 0 0 0-7.78z"></path>
                      </svg>
                      <span className={post.isLiked ? "text-red-500" : "text-gray-600"}>
                        {post.likes}
                      </span>
                    </button>
                    <button className="flex items-center gap-1 text-gray-600 text-sm">
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                        <path d="M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z"></path>
                      </svg>
                      <span>{post.comments}</span>
                    </button>
                  </div>
                  
                  {post.type === 'collaboration' && (
                    <Button variant="outline" size="sm">
                      Join Project
                    </Button>
                  )}
                </div>
              </div>
            </article>
          ))}
        </div>
      )}
    </div>
  );
}
